    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        String patientID = jTextField1.getText();
        
        try{
            Connection con = ConnectionProvider.getCon();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("select * from patient where patientID ='"+patientID+"'");
            if(rs.next()){
                jLabel9.setText("Name: "+rs.getString(2));
                flag=1;
            }
            
            else
            {
                JOptionPane.showMessageDialog(null, "Patient id doesn't exist.");
            }
            
        }catch(Exception e){
            System.out.println(e);
            JOptionPane.showMessageDialog(null, "Connection Error!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        if(flag==1){
            String patientID = jTextField1.getText();
            String symptom = jTextField2.getText();
            String diagnosis = jTextField3.getText();
            String medicines = jTextField4.getText();
            String wardReq;
            String typeWard;
            if(jCheckBox1.isSelected()){
                wardReq = "YES";
                typeWard = (String)jComboBox1.getSelectedItem(); 
            }else{
                wardReq = "NO";
                typeWard = "";
            }
            
            try {
    Connection con = ConnectionProvider.getCon();
    Statement st = con.createStatement();
    st.executeUpdate("INSERT INTO patientreport VALUES ('" + patientID + "', '" + symptom + "', '" + diagnosis + "', '" + medicines + "', '" + wardReq + "', '" + typeWard + "')");
    JOptionPane.showMessageDialog(null, "Successfully Updated");
    setVisible(false);
    new addDiagnosticForm().setVisible(true);
} catch(Exception e) {
    System.out.println(e);
    JOptionPane.showMessageDialog(null, "Connection Error");
}

        }else{
            JOptionPane.showMessageDialog(null, "Patient ID Field is empty");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

